import React, { Component } from 'react';
//import axios from 'axios';

//const API_URL = `https://jsonplaceholder.typicode.com/users`;
export default class BasicSearch extends Component {
    constructor(){
        super();
        this.state ={
            postData: [],
        };
    }

    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(users => {
                return users.json();
            }).then(data => {
                let postData = data.users.map((d) => {
                    return(
                        <div key={d.users}>
                            <p>{d.name}</p>
                        </div>
                    )
                })
                this.setState({ names: postData });
                console.log("state", this.state.names);
            })
    }

    render(){
        return(
            <div className="container">
                <div className="row">
                    {this.state.names}
                </div>
            </div>
        )
    }
}
