import React, { Component } from 'react';
import '../css/click.css';


export default class Click extends Component {

    constructor(props) {
        super(props);
        this.state = {
          data: [
            {name: 'Name 1', id: 133},
            {name: 'Name 2', id: 456}
          ]
        }
    }

    getComponent = (d, e) => {
        //const even = (element) => element % 2 === 0;
        //const found = d.find(id => id > 150);
        //const isBelowThreshold = (currentValue) => currentValue < 400;
        /*function getEven(num){
            let count = 0;
            while (num){
                count += num % 2 === 0;
                num = Math.floor(num/10);
            }
            return count;
        }*/
        function getOdd(num){
            let count = 0;
            while (num){
                count += num % 2 === 2;
                num = Math.floor(num/10);
            }
            return count;
        }
        console.log(e.currentTarget, d.id);

        //Set Style
        if (getOdd(d.id)) {
        //if (d.id === 456 ) {
            e.currentTarget.style.background = 'black'
            e.currentTarget.style.color = 'white'
            //style.top = ???
        }
        //console.log(i);
        //event.currentTarget.id === 123 ? this.style.backgroundColor = 'red' :  this.style.backgroundColor = 'blue';
        //event.currentTarget.style.backgroundColor = '#ccc';

    }

    render() { 
       //const {data} = this.state;
        return(       
            <div className="click-event-list">
                <ul>
                    {this.state.data.map(d => {
                        return(
                            <li key={d.id} onClick={this.getComponent.bind(this, d)}>{d.name}</li>
                        )}
                    )}
                </ul>
            </div>
        );
    }

}
