import React, { Component } from 'react';
//import hits from '../custom_data/image_search.json';
//var jsonData = require('../custom_data/image_search.json');

export default class LocalJson extends Component {
    state = {
        filter: "",
        data: [
          {
            id: 1,
            fname: "Jayne",
            lname: "Washington",
            email: "jaynewashington@exposa.com",
            gender: "female"
          },
          {
            id: 2,
            fname: "Peterson",
            lname: "Dalton",
            email: "petersondalton@exposa.com",
            gender: "male"
          },
          {
            id: 3,
            fname: "Velazquez",
            lname: "Calderon",
            email: "velazquezcalderon@exposa.com",
            gender: "male"
          },
          {
            id: 4,
            fname: "Norman",
            lname: "Reed",
            email: "normanreed@exposa.com",
            gender: "male"
          }
        ]
      };


      handleChange = event => {
        this.setState({ filter: event.target.value });
      };

      render() {
        const { filter, data } = this.state;
        const lowercasedFilter = filter.toLowerCase();
        const filteredData = data.filter(item => {
          return Object.keys(item).some(key =>
            item[key].toLowerCase().includes(lowercasedFilter)
          );
        });

        return (
          <div>
            <input value={filter} onChange={this.handleChange} />
            {filteredData.map(item => (
              <div key={item.id}>
                <div>
                  {item.fname} {item.lname} - {item.gender} - {item.email}
                </div>
              </div>
            ))}
          </div>
        );
      }
    }
