import React, { Component } from 'react';
import axios from 'axios';
import Suggestions from './suggestions';

//const { API_KEY } = process.env
//const API_KEY = 'AIzaSyCFRSbSTcTdOhZtEk6mrBBCibQMzWqAFlQ&cx=97095e5db09fb636d';
//const API_URL = 'http://api.musicgraph.com/api/v2/artist/suggest'
//const API_URL = 'https://www.googleapis.com/customsearch/v1/';
const API_URL = `https://jsonplaceholder.typicode.com/users`;
export default class BasicSearch extends Component {
  state = {
    query: '',
    results: []
  }

  getInfo = () => {
    //axios.get(`${API_URL}?api_key=${API_KEY}&prefix=${this.state.query}&limit=7`)
    //axios.get(`${API_URL}?key=${API_KEY}&prefix=${this.state.query}&limit=7&searchType=image`)
    axios.get(`${API_URL}`)
      .then(({ data }) => {
        this.setState({
          results: data.data
        })
      })
  }

  handleInputChange = () => {
    this.setState({
      query: this.search.value
    }, () => {
      if (this.state.query && this.state.query.length > 1) {
        if (this.state.query.length % 2 === 0) {
          this.getInfo()
        }
      } else if (!this.state.query) {
      }
    })
  }

  render() {
    return (
      <form>
        <input
          placeholder="Search for..."
          ref={input => this.search = input}
          onChange={this.handleInputChange}
        />
        <Suggestions results={this.state.results} />
      </form>
    )
  }
}
