import React, { Component } from 'react';
import '../css/click.css';


export default class Toggle extends Component {

    constructor(props) {
        super(props);
        this.state = {
          data: [
            {name: 'Name 1', id: 123},
            {name: 'Name 2', id: 456}
          ]
        }
    }

    getInitialState() {
        return {
            toggle: false
        };
    }

    clickHandler(e) {
        e.setState({ toggle: !this.state.toggle });
        e.style.backgroundColor = '#ccc';
    }

    /*getComponent(event) {
        console.log('li item clicked!');
        event.currentTarget.style.backgroundColor = '#ccc';
    }*/

    render() { 
        return(       
            <div className="click-event-list">
                <ul>
                    {this.state.data.map(d => {
                        return(
                            <li key={d.id} onClick={this.clickHandler.bind(this)}>{d.name}</li>
                        )}
                    )}
                </ul>
            </div>
        );
    }

}
