import React, { Component } from 'react';
//import hits from '../custom_data/image_search.json';
//var jsonData = require('../custom_data/image_search.json');
const names = ['James', 'John', 'Paul', 'Ringo', 'George'];
export default class LocalJson extends Component {
    constructor() {
        super();
        this.state = {search : ''}
    }
    handleChange = (e) =>{ 
        this.setState({search: e.target.value});
    }
    render (){
        return (
            <div>
            <input type="text" value={this.state.search} onChange={this.handleChange}/>

            {names.filter(name => name.toLowerCase().includes(this.state.search)).map(filteredName => (
                <li key={filteredName}>
                {filteredName}
                </li>
            ))}
            </div>
        );
    }
}
