class SearchBoxWithStyle extends React.Component {
  state = { mount: false };

  componentDidMount() {
    this.setState({ mount: true });
  }

  render() {
    return (
      <div>
        <Global
          styles={css`
            * {
              margin: 0;
              font-family: inherit;
              -webkit-font-smoothing: antialiased;
              -moz-osx-font-smoothing: grayscale;
