import React, { Component } from 'react';
import '../css/click.css';


export default class Click extends Component {

    constructor(props) {
        super(props);
        this.state = {
          data: [
            {name: 'Name 1', id: 123},
            {name: 'Name 2', id: 456}
          ]
        }
    }

    getComponent(e, data) {
        if(e.currentTarget = 'Name 1'){
            console.log(e.target.value, data);
        }

        //console.log(i);
        //event.currentTarget.id === 123 ? this.style.backgroundColor = 'red' :  this.style.backgroundColor = 'blue';
        //event.currentTarget.style.backgroundColor = '#ccc';

    }

    render() { 
        const {data} = this.state;
        return(       
            <div className="click-event-list">
                <ul>
                    {this.state.data.map(d => {
                        return(
                            <li key={d.id} onClick={this.getComponent.bind(this, data)}>{d.name}</li>
                        )}
                    )}
                </ul>
            </div>
        );
    }

}
